import * as React from "react";
import { cn } from "../../core/core";

export interface ButtonGroupProps
	extends React.HTMLAttributes<HTMLDivElement> {
	orientation?: "horizontal" | "vertical";
}

const ButtonGroup = React.forwardRef<HTMLDivElement, ButtonGroupProps>(
	({ className, orientation = "horizontal", ...props }, ref) => (
		<div
			ref={ref}
			role="group"
			className={cn(
				"inline-flex",
				orientation === "horizontal" ? "flex-row" : "flex-col",
				"[&>button:not(:first-child)]:rounded-l-none",
				"[&>button:not(:last-child)]:rounded-r-none",
				"[&>button:not(:first-child)]:-ml-px",
				orientation === "vertical" && "[&>button:not(:first-child)]:-mt-px",
				orientation === "vertical" && "[&>button:not(:first-child)]:rounded-t-none",
				orientation === "vertical" && "[&>button:not(:last-child)]:rounded-b-none",
				className,
			)}
			{...props}
		/>
	),
);
ButtonGroup.displayName = "ButtonGroup";

export { ButtonGroup };
