import { Tooltip as BaseTooltip } from "@base-ui-components/react";
import * as React from "react";
import {
	cn,
	colors,
	padding,
	typography,
	utilities,
	shadows,
	shape,
} from "../../core/core";

const tooltipStyles = {
	content: cn(
		shape.rounded,
		colors.backgrounds.surface,
		padding.small,
		typography.size.small,
		utilities.zIndex.modal,
		shadows.depth.medium,
		"max-w-xs",
	),
	arrow: cn(colors.backgrounds.surface),
};

const Tooltip = BaseTooltip.Root;
const TooltipTrigger = BaseTooltip.Trigger;

const TooltipContent = React.forwardRef<
	React.ElementRef<typeof BaseTooltip.Popup>,
	React.ComponentPropsWithoutRef<typeof BaseTooltip.Popup>
>(({ className, ...props }, ref) => (
	<BaseTooltip.Portal>
		<BaseTooltip.Positioner>
			<BaseTooltip.Popup
				ref={ref}
				className={cn(tooltipStyles.content, className)}
				{...props}
			>
				<BaseTooltip.Arrow className={tooltipStyles.arrow} />
			</BaseTooltip.Popup>
		</BaseTooltip.Positioner>
	</BaseTooltip.Portal>
));
TooltipContent.displayName = "TooltipContent";

export { Tooltip, TooltipContent, TooltipTrigger };
